package mesh;

/**
 * @author David Cornette
 *
 */
public class Vec4 {

	double x;
	double y;
	double z;
	double w;
	/**
	 * 
	 */
	public Vec4() {
		super();
		// TODO Auto-generated constructor stub
	}
	/**
	 * @param x
	 * @param y
	 * @param z
	 */
	public Vec4(double x, double y, double z) {
		super();
		// TODO Auto-generated constructor stub
		this.x = x;
		this.y = y;
		this.z = z;
	}
	/**
	 * @param x
	 * @param y
	 * @param z
	 * @param w
	 */
	public Vec4(double x, double y, double z, double w) {
		super();
		// TODO Auto-generated constructor stub
		this.x = x;
		this.y = y;
		this.z = z;
		this.w = w;
	}
	/**
	 * @return Returns the w.
	 */
	public double getW() {
		return w;
	}
	/**
	 * @param w The w to set.
	 */
	public void setW(double w) {
		this.w = w;
	}
	/**
	 * @return Returns the x.
	 */
	public double getX() {
		return x;
	}
	/**
	 * @param x The x to set.
	 */
	public void setX(double x) {
		this.x = x;
	}
	/**
	 * @return Returns the y.
	 */
	public double getY() {
		return y;
	}
	/**
	 * @param y The y to set.
	 */
	public void setY(double y) {
		this.y = y;
	}
	/**
	 * @return Returns the z.
	 */
	public double getZ() {
		return z;
	}
	/**
	 * @param z The z to set.
	 */
	public void setZ(double z) {
		this.z = z;
	}
	
	
}
